import React, { useState } from "react";
import Calendar from "./Calendar";
import Dashboard from "./Dashboard";
import RewardAnimation from "./RewardAnimation";
import { sampleCalendarData, sampleActivityData } from "./data";

function App() {
  const [animationData, setAnimationData] = useState({ visible: false });

  return (
    <div>
      <h1>プロトタイプ確認</h1>
      <Calendar data={sampleCalendarData} setAnimationData={setAnimationData} />
      <Dashboard data={sampleActivityData} />
      <RewardAnimation animationData={animationData} />
      <button onClick={() => setAnimationData({ visible: true, reward: { points: 50 }, rarity: "rare" })}>
        擬似通知
      </button>
    </div>
  );
}

export default App;
